def stepname = context.testCase.getTestStepAt(context.getCurrentStepIndex()).getLabel().replaceAll("\\s", "")
log.info ("stepname " + stepname)

// concatenate propertyname :
testcasePropertyName =  "ExpectedResponse" + stepname
//log.info ("Property name " + testcasePropertyName)

// Obtain the testcase property and convert it to lowercase
def testcaseProperty = context.testCase.getPropertyValue(testcasePropertyName) 
assert testcaseProperty != null : "The test step is null."
testcaseProperty = testcaseProperty.toLowerCase()
//log.info ("Property value " + testcaseProperty)

// Obtain the response message and convert it to lowercase
def responseMessage = messageExchange.responseContent.toLowerCase()
assert responseMessage != null : "The test step is null."
responseMessage = responseMessage.toLowerCase()
//log.info ("responseMessage " + responseMessage)

// Check if the response message contains the text from the testcase property
assert responseMessage.contains(testcaseProperty) : "The response message does not contain the expected text."

