con:config.script:
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

    static def formatJsonForCurl(log, input) {
        if (System.properties['os.name'].toLowerCase().contains('windows')) {
        	return input.replace("\"", "\"\"\"")
        	/*
            assert(input != null),"Error:formatJsonForCurl: input string is null."
            assert(input.contains("[") && input.contains("]")),"Error:formatJsonForCurl: input string is corrupted."
            def intermediate = input.substring(input.indexOf("[") + 1, input.lastIndexOf("]")).replace("\"", "\"\"\"")
            return "[" + intermediate + "]"*/
        }
        return input
    }

		def urlToSMP=SMP.getSoapUiCustomProperty(log, context, "url", "project",false)
		def urlExt="/ui/internal/rest/property"
def newValue=".*"
def updatedProp=[:]
def propName="identifiersBehaviour.ParticipantIdentifierScheme.validationRegex"
def propMetadata=SMP.getSmpConfigPropertyMeta(log, context, propName)
log.info propMetadata

propMetadata.each{index, val ->
	updatedProp[index]=val
}
updatedProp["status"]=1
updatedProp["deleted"]=false
updatedProp["value"]=newValue
def updatedPropJson=JsonOutput.toJson(updatedProp).toString()
log.info updatedProp

log.info updatedPropJson

def commandString=["curl", urlToSMP+urlExt,
                                         "--cookie", context.expand('${projectDir}') + File.separator + "cookie.txt",
                                         "-H", "Content-Type: application/json",
                                         "-H","X-XSRF-TOKEN: " + SMP.returnXsfrToken(log, context, "system", "123456"),
                                         "-X", "PUT",
                                         "--data", formatJsonForCurl(log,updatedPropJson),
                                         "-v"]


 def commandResult=SMP.runCommandInShell(log, commandString)   
log.info commandResult[0]
log.info commandResult[1]
                                      log.info "done"

