con:config.script:
import com.eviware.soapui.impl.wsdl.teststeps.RestTestRequestStep
def headers=null
testRunner.testCase.testSuite.getTestCaseList().each { testCase ->
	testCase.getTestStepList().each{testStep ->
		if (testStep instanceof RestTestRequestStep) {
			headers=testStep.httpRequest.requestHeaders
			headers.put("Resource-Group", "\${#TestSuite#groupDef}")
			testStep.httpRequest.requestHeaders = headers
			log.info "headers="+headers
		} 
	}
}
log.info "done"

